import{_ as r,c as d,o,ah as s,aY as n,aZ as g,a_ as e}from"./chunks/framework.CqUZohul.js";const u=JSON.parse('{"title":"ECO VTX","description":"","frontmatter":{},"headers":[],"relativePath":"ru/eco-bundle.md","filePath":"ru/eco-bundle.md","lastUpdated":1756798263000,"contributors":[]}'),a={name:"ru/eco-bundle.md"};function l(i,t,c,p,_,b){return o(),d("div",null,[...t[0]||(t[0]=[s('<h1 id="eco-vtx" tabindex="-1">ECO VTX <a class="header-anchor" href="#eco-vtx" aria-label="Permalink to “ECO VTX”">​</a></h1><p><strong>HDZero ECO</strong> создан для того, чтобы сделать <strong>цифру</strong> доступнее большему количеству пилотов, предлагая <strong>недорогую</strong>, <strong>лёгкую</strong> и <strong>прочную</strong> альтернативу аналоговому видео.<br> Цифровой видеосигнал обеспечивает <strong>стабильную цветопередачу</strong> и изображение <strong>720p</strong> с прогрессивной развёрткой и <strong>низкой задержкой</strong>, за мы которую мы так любим <strong>HDZero</strong>.</p><p>Этот <strong>бюджетный передатчик</strong> — настоящий прорыв:<br> это первый комплект <strong>VTX + камера</strong>, использующий <strong>HD-композитный видеосигнал</strong>, который <strong>исключает необходимость хрупкого MIPI-кабеля</strong>.</p><p>Система <strong>ECO FPV</strong> была специально разработана для <strong>ультралёгких дронов</strong>, таких как <strong>Тинивупы</strong>, где важен <strong>каждый миллиграмм</strong>.<br> Камера имеет <strong>огромный вертикальный угол обзора 98°</strong>, что идеально для <strong>тинивуп гонок</strong>.</p><p>Вес <strong>ECO VTX</strong> — около <strong>4.5 г</strong>,<br> камера (с проводами) — примерно <strong>1.6 г</strong>,<br> а комплектная дипольная антенна — <strong>0.2 г</strong>.<br> Вся система <strong>ECO Bundle</strong> весит <strong>всего ~6.3 г</strong>.</p><img src="'+n+'" id="image18"><table id="table8"><tr><td>1</td><td>Разъём обновления прошивки</td></tr><tr><td>2</td><td>Пины для пайки камеры</td></tr><tr><td>3</td><td>Разъём для пультика VTX</td></tr><tr><td>4</td><td>Разъём питания (4–12В) / UART</td></tr><tr><td>5</td><td>Разъём U.FL</td></tr></table><table id="table9"><tr><td>Разъём камеры</td><td>Цвет провода</td><td>Подключается к</td></tr><tr><td>5V</td><td>Красный</td><td>Питание камеры</td></tr><tr><td>GND</td><td>Красный</td><td>Земля камеры</td></tr><tr><td>CAM</td><td>Жёлтый</td><td>Видеосигнал камеры</td></tr><tr><td>OSD</td><td>Белый</td><td>Меню камеры</td></tr><tr><td>SCL</td><td></td><td>Не используется</td></tr><tr><td>SDA</td><td></td><td>Не используется</td></tr></table><img src="'+g+'" id="image19"><table id="table10"><tr><td>Разъём питания/UART</td><td>Цвет провода</td><td>Подключается к</td></tr><tr><td>V</td><td>Красный</td><td>Питание</td></tr><tr><td>G</td><td>Чёрный</td><td>Земля</td></tr><tr><td>T</td><td>Белый</td><td>ПК.RX</td></tr><tr><td>R</td><td>Жёлтый</td><td>ПК.TX</td></tr></table><img src="'+e+'" id="image20"><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>Примечания:</p><ul><li>При использовании <strong>питания от BEC полетника</strong> рекомендуется следующее: <ul><li>Если BEC выдаёт только <strong>5В</strong>, он должен обеспечивать <strong>минимум 2.0А</strong>.</li><li>Для плат серии <strong>1R2</strong> рекомендуется подключать BEC <strong>к конденсатору</strong>, как показано на схеме.</li><li>Для плат <strong>не имеющих маркировку 1R2</strong> рекомендуется подключать BEC <strong>на пэд BEC</strong>, как показано на схеме.</li></ul></li><li>Если <strong>BEC отсутствует</strong>, используйте <strong>VBAT</strong> (<strong>1S или 2S</strong>) для питания.</li></ul></div>',12)])])}const T=r(a,[["render",l]]);export{u as __pageData,T as default};
