import{_ as a,I as t,c as n,o as i,ak as e,J as o,cs as g,ct as h,cu as d,cv as c}from"./chunks/framework.wh6QqFPS.js";const F=JSON.parse('{"title":"Общие рекомендации","description":"","frontmatter":{},"headers":[],"relativePath":"ru/vtx-general.md","filePath":"ru/vtx-general.md","lastUpdated":1756966431000}'),p={name:"ru/vtx-general.md"};function m(b,r,T,u,f,_){const s=t("NolebaseGitContributors"),l=t("NolebaseGitChangelog");return i(),n("div",null,[r[0]||(r[0]=e('<h1 id="общие-рекомендации" tabindex="-1">Общие рекомендации <a class="header-anchor" href="#общие-рекомендации" aria-label="Permalink to “Общие рекомендации”">​</a></h1><h2 id="обновление-прошивки-vtx" tabindex="-1">Обновление прошивки VTX <a class="header-anchor" href="#обновление-прошивки-vtx" aria-label="Permalink to “Обновление прошивки VTX”">​</a></h2><p>Обновите прошивку <strong>VTX</strong> до последней версии, чтобы обеспечить совместимость с актуальными камерами, прошивками полётных контроллеров и видеоприёмниками.<br> Необязательно, чтобы версия прошивки <strong>Передатчика</strong> совпадала с версией <strong>Приемника</strong>, но это <strong>рекомендуется</strong>.<br> См. <a href="./vtx-firmware-update.html">инструкцию по обновлению прошивки</a>.</p><h2 id="внимание-разъемы-u-fl-хрупкие" tabindex="-1">Внимание: разъёмы U.FL хрупкие <a class="header-anchor" href="#внимание-разъемы-u-fl-хрупкие" aria-label="Permalink to “Внимание: разъёмы U.FL хрупкие”">​</a></h2><p>Все <strong>HDZero VTX</strong> используют разъём <strong>U.FL</strong> — он компактный и лёгкий,<br> но <strong>механически очень хрупкий</strong>. Обращайтесь с ним <strong>аккуратно</strong>.</p><p>Советы по установке и снятию антенны:</p><ul><li><p>Используйте прижимную <strong>латунную</strong> или <strong>пластиковую планку</strong>, если она есть в комплекте.</p></li><li><p>Если планка не используется, <strong>никогда</strong> не тяните кабель антенны прямо вбок.<br> Вместо этого направляйте кабель к центру платы и фиксируйте его одним из способов:</p><ul><li><strong>Клеем клеем</strong> (например, T7000) <ul><li>Не наносите клей на горячие элементы платы.</li><li>Лучше наносить на разьем и вокруг него.</li></ul></li><li><strong>Каптоновым скотчем</strong> — закрепите кабель на поверхности <strong>VTX</strong>.</li><li><strong>Стяжкой</strong> — аккуратно фиксируем к ближайшей стойке (не пережимайте кабель!).</li></ul></li><li><p>Если вам не нужен лучший сигнал и минимальный вес, то можно использовать <strong>переходник U.FL → SMA</strong>.<br> Это позволяет надёжно зафиксировать SMA-разъём на раме,<br> чтобы антенна не дергала разъём <strong>U.FL</strong>.<br> Частая причина проблем с SMA — отлом коаксиала у основания разъёма, если это произойдет то будет плохой видеосигнал.</p></li><li><p>При снятии антенны <strong>не тяните её вбок</strong>.<br> Используйте пинцет и аккуратно поднимайте разъём <strong>строго вверх</strong>, может быть немного под углом.</p></li></ul><h2 id="питание-vtx-использование-bec" tabindex="-1">Питание VTX: использование BEC <a class="header-anchor" href="#питание-vtx-использование-bec" aria-label="Permalink to “Питание VTX: использование BEC”">​</a></h2><p>Рекомендуется питать <strong>VTX</strong> через <strong>BEC</strong> (Понижайка/Повышайка),<br> чтобы избежать <strong>скачков напряжения</strong>, которые могут повредить плату.</p><p>Хотя можно запитать <strong>VTX</strong> напрямую от аккумулятора (<strong>VBAT</strong>),<br><strong>BEC обеспечивает стабильное питание</strong> и защищает от <strong>перенапряжения</strong> и <strong>просадок</strong>.</p><p>Правила выбора BEC:</p><ul><li>BEC должен выдавать напряжение <strong>в диапазоне, допустимом для VTX</strong>.</li><li>BEC должен обеспечивать <strong>достаточный ток</strong> для питания передатчика.</li><li>BEC может быть <strong>встроенным в ПК</strong> или <strong>отдельным</strong>.</li><li>Проверяйте характеристики BEC и сравнивите их со <a href="./vtx-summary.html">Спецификацией VTX</a>.</li></ul><p><strong>Важно:</strong> все полетники со встроенным BEC для <strong>DJI</strong> подходят и для <strong>HDZero</strong>.</p><p>Итог:</p><ul><li>Сначала проверьте, можно ли питать <strong>VTX</strong> от встроенного <strong>BEC</strong> на FC.</li><li>Если нельзя — используйте отдельный BEC.</li><li>Если BEC нет — крайний вариант подключение к <strong>VBAT</strong>.</li><li>Все <strong>HDZero VTX</strong>, кроме <strong>Freestyle V2</strong>, <strong>не поддерживают питание от 6S</strong>.</li></ul><h2 id="рекомендации-по-установке-vtx" tabindex="-1">Рекомендации по установке VTX <a class="header-anchor" href="#рекомендации-по-установке-vtx" aria-label="Permalink to “Рекомендации по установке VTX”">​</a></h2><p>Важно оставлять зазор между <strong>VTX</strong> и платами <strong>ESC/FC</strong>:</p><ul><li>RF-часть VTX чувствительна к шумам от <strong>ESC</strong> и моторов.</li><li>Сигнал может отражаться от поверхностей <strong>карбона</strong> и ухудшать работу усилителя.</li></ul><p>Правильная установка:</p><ul><li><strong>Не приклеивайте VTX к раме скотчем</strong> <em>(кроме Freestyle V2)</em>.</li><li>Если ставите VTX сверху стака — оставьте <strong>5 мм</strong> до верхней карбоновой пластины.</li><li>Избегайте установки VTX <strong>в середине стака</strong>.</li><li>Если ставите VTX снизу — монтируйте его <strong>вверх ногами</strong><br> (<strong>U.FL-разъём вниз</strong>) и оставляйте <strong>5 мм зазора</strong> до нижней пластины.</li></ul><h2 id="настроика-osd-msp" tabindex="-1">Настройка OSD/MSP <a class="header-anchor" href="#настроика-osd-msp" aria-label="Permalink to “Настройка OSD/MSP”">​</a></h2><h3 id="_1-требования-к-прошивкам" tabindex="-1">1. Требования к прошивкам <a class="header-anchor" href="#_1-требования-к-прошивкам" aria-label="Permalink to “1. Требования к прошивкам”">​</a></h3><ul><li><strong>Betaflight</strong> ≥ 4.4.0</li><li><strong>INAV</strong> ≥ 4.1</li><li><strong>KISS ULTRA</strong> ≥ 2.0.1-B35</li><li><strong>FlightOne</strong> ≥ 10.1.1.5576 | 10.1 Alpha 29</li></ul><h3 id="_2-обновите-прошивку-передатчика-и-приемника" tabindex="-1">2. Обновите прошивку передатчика и приемника <a class="header-anchor" href="#_2-обновите-прошивку-передатчика-и-приемника" aria-label="Permalink to “2. Обновите прошивку передатчика и приемника”">​</a></h3><p>Загрузите последнюю версию:<br><a href="https://www.hd-zero.com/document" target="_blank" rel="noreferrer">www.hd-zero.com/document</a></p><h3 id="_3-подключение-vtx-к-пк" tabindex="-1">3. Подключение VTX к ПК <a class="header-anchor" href="#_3-подключение-vtx-к-пк" aria-label="Permalink to “3. Подключение VTX к ПК”">​</a></h3><p>См. <a href="./vtx-installation.html">схему подключения</a>.</p><h3 id="_4-настроика-betaflight" tabindex="-1">4. Настройка Betaflight <a class="header-anchor" href="#_4-настроика-betaflight" aria-label="Permalink to “4. Настройка Betaflight”">​</a></h3><ol><li>Подключите FC к ПК через USB.</li><li>Откройте <strong>Betaflight Configurator</strong>.</li><li>Перейдите во вкладку <strong>Поррты</strong>.</li><li>Включите <strong>MSP + DisplayPort</strong> на том <strong>UART</strong>, куда подключен VTX.</li><li>Сохраните и перезагрузите FC.</li></ol><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>💡 <strong>Примечание:</strong> SoftSerial для VTX <strong>не поддерживается</strong>.</p><p>Убедитесь что прошивка Betaflight собрана с опциями HD OSD и VTX</p></div><p>Пример использования <strong>UART5</strong> для VTX (MSP + Displayport):<br><img src="'+g+'" id="image6"></p><h3 id="_5-настроика-osd" tabindex="-1">5. Настройка OSD <a class="header-anchor" href="#_5-настроика-osd" aria-label="Permalink to “5. Настройка OSD”">​</a></h3><ol><li><p>Перейдите во вкладку <strong>Configuration</strong>.</p></li><li><p>Включите функцию <strong>OSD</strong>.</p></li><li><p>Нажмите <strong>Save</strong>.<br><img src="'+h+'" id="image7"></p></li><li><p>Перейдите во вкладку <strong>OSD</strong> и настройте отображаемые элементы.</p></li><li><p>Снова <strong>Save</strong>.</p></li></ol><h2 id="таблицы-vtx" tabindex="-1">Таблицы VTX <a class="header-anchor" href="#таблицы-vtx" aria-label="Permalink to “Таблицы VTX”">​</a></h2><p><strong>Передатчики HDZero</strong> не требуют ручной настройки таблиц каналов —<br> они загружаются автоматически при включении.</p><h2 id="smartaudio-не-поддерживается" tabindex="-1">SmartAudio (не поддерживается) <a class="header-anchor" href="#smartaudio-не-поддерживается" aria-label="Permalink to “SmartAudio (не поддерживается)”">​</a></h2><p><strong>HDZero VTX</strong> больше <strong>не поддерживает SmartAudio</strong>.<br> Используйте <strong>MSP</strong>.</p><h2 id="стик-команды" tabindex="-1">Стик команды <a class="header-anchor" href="#стик-команды" aria-label="Permalink to “Стик команды”">​</a></h2><p><img src="'+d+'" id="image8"><div class="page"></div></p><h2 id="меню-vtx" tabindex="-1">Меню VTX <a class="header-anchor" href="#меню-vtx" aria-label="Permalink to “Меню VTX”">​</a></h2><p>Все <strong>HDZero VTX</strong> имеют меню с настройками,<br> включая каналы, мощность и специальные режимы.</p><img src="'+c+'" id="image9"><h3 id="channel" tabindex="-1">CHANNEL <a class="header-anchor" href="#channel" aria-label="Permalink to “CHANNEL”">​</a></h3><p>Поддерживаются каналы:<br><strong>R1-R8, E1, F1, F2, F4</strong>.<br> Если разблокировать <strong>Low Band</strong>, добавятся <strong>L1-L8</strong>.</p><h3 id="power" tabindex="-1">POWER <a class="header-anchor" href="#power" aria-label="Permalink to “POWER”">​</a></h3><p>Доступные значения выходной мощности:<br><strong>25 мВт</strong>, <strong>200 мВт</strong> <em>(500 мВт / MAX для некоторых VTX)</em>.<br> Фактическая мощность зависитит от настроек тут, либо в полетнике.</p><h3 id="pit-mode-рекомендуется-off-если-вы-не-знаете-что-это" tabindex="-1">PIT_MODE <em>(рекомендуется OFF, если вы не знаете что это)</em> <a class="header-anchor" href="#pit-mode-рекомендуется-off-если-вы-не-знаете-что-это" aria-label="Permalink to “PIT_MODE (рекомендуется OFF, если вы не знаете что это)”">​</a></h3><ul><li><strong>OFF</strong> — Пит выключен и передатчик работает на заданной мощности.</li><li><strong>P1MW</strong> — выходная мощность <strong>0.1 мВт</strong> (не мешает другим пилотам).<br> После арма мощность автоматически возвращается к заданной ранее.</li><li><strong>0MW</strong> — полностью отключает передатчик.<br> После арма возвращается к заданной мощности.</li></ul><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>После первого арма VTX не вернётся в PIT MODE до перезапуска питания.</p></div><h3 id="lp-mode-рекомендуется-off" tabindex="-1">LP_MODE <em>(рекомендуется OFF)</em> <a class="header-anchor" href="#lp-mode-рекомендуется-off" aria-label="Permalink to “LP_MODE (рекомендуется OFF)”">​</a></h3><ul><li><strong>OFF</strong> — работает на заданной мощности.</li><li><strong>ON</strong> — Если PIT выключен и дрон не заармлен, то мощность ограничивается <strong>25 мВт</strong>,<br> при арме возвращается к заданной.</li><li><strong>1ST</strong> — Если PIT выключен мощность ограничена <strong>25 мВт</strong>, при арме вернется на заданную</li></ul><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>После первого арма VTX не вернётся в LP MODE до переподключения питания.</p></div><h3 id="offset-25mw-по-умолчанию-0" tabindex="-1">OFFSET_25MW <em>(по умолчанию 0)</em> <a class="header-anchor" href="#offset-25mw-по-умолчанию-0" aria-label="Permalink to “OFFSET_25MW (по умолчанию 0)”">​</a></h3><p>Точная калибровка мощности <strong>25 мВт</strong> в пределах <strong>[-10; 10]</strong><br> (шаг ≈ <strong>0.1 дБ</strong>).</p><h3 id="team-race-для-командных-гонок" tabindex="-1">TEAM_RACE <em>(для командных гонок)</em> <a class="header-anchor" href="#team-race-для-командных-гонок" aria-label="Permalink to “TEAM_RACE (для командных гонок)”">​</a></h3><ul><li><strong>OFF</strong> — поведение VTX определяется другими настройками.</li><li><strong>MODE1</strong> — VTX не передаёт сигнал после включения.<br> Передача включается: <ol><li>при арме,</li><li>через жест стиками,</li><li>при изменении мощности через полетник.</li></ol></li><li><strong>MODE2</strong> — аналогично MODE1, но при разрыве связи с полетником автоматически выключает RF до восстановления.</li></ul><h3 id="стик-команды-1" tabindex="-1">Стик команды <a class="header-anchor" href="#стик-команды-1" aria-label="Permalink to “Стик команды”">​</a></h3><p>Используйте <a href="#stick-command-gestures">стик команды</a> для переключения режимов.</p><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p><strong>Примечания:</strong></p><ul><li>VTX может <strong>греться даже на P1mW</strong> → лучше ставить <strong>0мВт</strong>, если дрон не летает.</li><li>Видео восстанавливается автоматически после арма.</li></ul></div><h2 id="рекомендованные-настроики-для-гонок-тренировок" tabindex="-1">Рекомендованные настройки для гонок / тренировок <a class="header-anchor" href="#рекомендованные-настроики-для-гонок-тренировок" aria-label="Permalink to “Рекомендованные настройки для гонок / тренировок”">​</a></h2><ul><li><strong>Channel:</strong> нужный вам канал</li><li><strong>Power:</strong> 25 мВт</li><li><strong>PIT_MODE:</strong> OFF</li><li><strong>LP_MODE:</strong> OFF</li><li><strong>OFFSET_25MW:</strong> 0</li><li><strong>TEAM_RACE:</strong> OFF</li><li><strong>SHORTCUT:</strong> OPT_A</li></ul><h2 id="режим-командных-гонок" tabindex="-1">Режим командных гонок <a class="header-anchor" href="#режим-командных-гонок" aria-label="Permalink to “Режим командных гонок”">​</a></h2><ul><li><strong>Channel:</strong> нужный вам канал</li><li><strong>Power:</strong> 25 мВт</li><li><strong>PIT_MODE:</strong> OFF</li><li><strong>LP_MODE:</strong> OFF</li><li><strong>OFFSET_25MW:</strong> 0</li><li><strong>TEAM_RACE:</strong> MODE1</li><li><strong>SHORTCUT:</strong> OPT_A</li></ul><p>После включения питания <strong>VTX в 0мВт</strong> — сигнал пойдёт только после арма или жеста стиками.<br> Если в гонке вы крашнулись, чтобы не мешать товарищам по каналу:</p><ol><li>Дизармите дрон.</li><li>Введите VTX в <strong>0мВт</strong> через жест стиками <em>(OPT-A / )</em>.</li><li>Если ПК не отвечает — VTX сам отключит RF через несколько секунд.</li></ol>',65)),o(s),o(l)])}const V=a(p,[["render",m]]);export{F as __pageData,V as default};
