import{_ as e,c as r,o,ah as i}from"./chunks/framework.D4EhY9MR.js";const g=JSON.parse('{"title":"HDZero Whoop Lite Video Transmitter","description":"","frontmatter":{},"headers":[],"relativePath":"whoop-lite.md","filePath":"whoop-lite.md","lastUpdated":1756546334000}'),n={name:"whoop-lite.md"};function a(l,t,d,s,p,h){return o(),r("div",null,[...t[0]||(t[0]=[i('<h1 id="hdzero-whoop-lite-video-transmitter" tabindex="-1">HDZero Whoop Lite Video Transmitter <a class="header-anchor" href="#hdzero-whoop-lite-video-transmitter" aria-label="Permalink to “HDZero Whoop Lite Video Transmitter”">​</a></h1><p>The HDZero Whoop lite VTX is a digital HD 720p 60fps video transmitter capable of delivering up to 200mw on 5.8GHz.</p><p>The power input range is 2.8V – 13V (1S-3S).</p><ul><li><strong>This VTX does not have reserve polarity protection circuit, it will be permanently damaged if voltage input is reservedly connected. This is number one failure cause.</strong></li><li>When it is powered by a 5V pad of FC/BEC, make sure that 5V has 1.5A current output.</li><li>When it is powered by an 1S battery directly, note that a rapid throttle up may cause voltage drop below to 2.8V instantly, and that will cause video drop.</li><li>There is no need for an external capacitor when it connects with FC/BEC or 1S battery. However, it is strongly suggested to put on capacitor when powered by a 3S battery.</li></ul><p>This VTX has protection cases mounted on both sides. These metal cases are grounded, and to provide protection from crash. They can be removed for tiny whoop quad which cares about every added gram.</p><p>This VTX includes M3/M2 gummies for soft mounting, and zip ties for antenna retention. Though the VTX can be mounted on a racing and other high-speed quad, it is highly recommended to consider HDZero Race (V1 or V2) for such application.</p><img src="https://raw.githubusercontent.com/hd-zero/hdzero-vtx-docs/main/img/HDZero_WhoopLite_intro.png" style="width:100;"><table tabindex="0"><thead><tr><th style="text-align:center;">No.</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td style="text-align:left;">MIPI Connector</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:left;">Status LEDs</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:left;">Power/UART/SA solder pads</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:left;">u.FL Connector</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:left;">Protection Case</td></tr><tr><td style="text-align:center;">6</td><td style="text-align:left;">Protection case clipper</td></tr></tbody></table><p>Solder pad:</p><table tabindex="0"><thead><tr><th style="text-align:center;">Pad</th><th style="text-align:left;">Function</th></tr></thead><tbody><tr><td style="text-align:center;">G</td><td style="text-align:left;">Ground</td></tr><tr><td style="text-align:center;">V</td><td style="text-align:left;">2.8V-13V</td></tr><tr><td style="text-align:center;">RX</td><td style="text-align:left;">FC.TX</td></tr><tr><td style="text-align:center;">TX</td><td style="text-align:left;">FC.RX</td></tr><tr><td style="text-align:center;">SA</td><td style="text-align:left;">FC.SA(TX), Optional</td></tr></tbody></table><p><strong>Notes:</strong></p><ul><li>Read first: <strong>General Consideration for VTX installation</strong></li><li>This VTX does not support using keypad to tune channel and RF output power level. Ir is designed to work with flight controller.</li></ul>',12)])])}const y=e(n,[["render",a]]);export{g as __pageData,y as default};
