import{_ as n,I as t,c as l,o as r,ak as s,J as i,b3 as c}from"./chunks/framework.Cyj2LXny.js";const C=JSON.parse('{"title":"简介","description":"","frontmatter":{},"headers":[],"relativePath":"zh/event-introduction.md","filePath":"zh/event-introduction.md","lastUpdated":1757336659000}'),d={name:"zh/event-introduction.md"};function p(_,e,D,m,h,u){const o=t("NolebaseGitContributors"),a=t("NolebaseGitChangelog");return r(),l("div",null,[e[0]||(e[0]=s('<h1 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to “简介”">​</a></h1><div style="display:flex;align-items:center;justify-content:space-around;margin:40px;"><img src="'+c+'"></div><p>与竞速飞手和 MultiGP 赛事协调员合作设计，HDZero Event VRX 可以无缝集成 HDZero 到比赛 DVR 和直播设置中。</p><p>Event VRX 会根据接收到的视频信号在模拟和 HDZero 视频之间自动切换。这意味着不需要单独的软件来切换录像视频，也不需要提前进行特殊设置。</p><p>Event VRX 可以在 HDMI 和 CVBS 输出 HDZero 和模拟视频。这意味着你可以使用现有的传统模拟视频采集设备，例如 Owl DVR 盒子或模拟采集卡，从而保持低成本和低复杂度。</p><p>Event VRX 将四个双天线 HDZero 接收机集成在一个外壳中，从而降低了成本并简化了安装。双天线 HDZero 接收机的抗噪能力比带有 4 根天线的头显 VRX 稍差，但在户外比赛中依然有出色的视频接收效果。还包括 8 个模拟接收机，以分集方式配置（每个频道 2 个模拟接收机）。</p><h2 id="功能" tabindex="-1">功能 <a class="header-anchor" href="#功能" aria-label="Permalink to “功能”">​</a></h2><ul><li>为比赛提供的无缝 HDZero 和模拟自动切换接收机</li><li>每个盒子有 4 个频道，每个频道可以接收 HDZero 或模拟信号</li><li>4 路 HDMI 和 4 路 CVBS（复合模拟）输出，可以连接到 HDMI 采集卡或传统模拟 DVR 录像机</li><li>数字和模拟信号都可以输出到 HDMI 和 CVBS —— 系统根据信号选择视频源</li><li>2 个 SMA 天线接口，所有 4 个频道共享，因此只需要 2 根天线即可接收 4 路视频</li><li>结实的钢制外壳，专为安装在塔架上设计</li><li>2D 去隔行器改善 HDMI 输出下的模拟视频质量</li><li>OLED 配置屏幕用于频道和设置更改</li><li>功耗 30W，8-20V DC 输入</li><li>附带 AC 转 DC 电源适配器</li><li>通过 USB 和附带的 Windows PC 软件进行固件更新</li></ul>',8)),i(o),i(a)])}const v=n(d,[["render",p]]);export{C as __pageData,v as default};
