import{_ as a,c as d,o as l,ah as r,bq as i}from"./chunks/framework.CqUZohul.js";const _=JSON.parse('{"title":"简介","description":"","frontmatter":{},"headers":[],"relativePath":"zh/halo-esc-introduction.md","filePath":"zh/halo-esc-introduction.md","lastUpdated":1757232363000,"contributors":[]}'),e={name:"zh/halo-esc-introduction.md"};function o(p,t,n,s,c,m){return l(),d("div",null,[...t[0]||(t[0]=[r('<h1 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to “简介”">​</a></h1><div style="display:flex;align-items:center;justify-content:space-around;margin:40px;"><img src="'+i+'" id="image35"></div><p>HDZero Halo 电调是一款适用于竞速和自由飞行无人机应用的 4in1 电调。它的设计注重卓越的可靠性、抗撞击性和高电流输出，非常适合无人机竞速的需求。</p><p>Halo 电调采用分体板设计，具有独立的 MCU/驱动板和高压电源板，集成了 24 个 MOSFET 和一块 8 层 PCB，每层铜厚超过 3 盎司，以优化电流流动和热效率。后置铜条进一步增强了电流传输和散热性能。每个电机可在 30 秒内承受超过 70A 的输出，峰值可达到 100A，持续 3 秒。</p><p>Halo 电调为 MCU 提供异常干净的电源，保护它们免受因电机突然加速或减速而引发的电压峰值。这项关键保护帮助防止 MCU 电压骤降或死机，这些问题通常是烧毁电机、MOSFET 故障和飞行失败的根本原因。</p><p>Halo 电调采用防护涂层，增强了其对潮湿、灰尘和腐蚀的抵抗力。</p><p>Halo 电调提供两种版本，BLHeli32 和 AM32，以满足不同飞行员的需求。</p><h2 id="规格" tabindex="-1">规格 <a class="header-anchor" href="#规格" aria-label="Permalink to “规格”">​</a></h2><table id="table3"><tr><td>型号</td><td colspan="2">HDZero Halo 4in1 70A 电调</td></tr><tr><td>MCU</td><td colspan="2">AT32F421 (120MHz)</td></tr><tr><td>固件</td><td>BLHeli 32.10</td><td>AM32 2.18<br>目标：HDZ_ESC_1R0_F421</td></tr><tr><td>输入电压</td><td colspan="2">DC 9V – 40V (3S – 8S)</td></tr><tr><td>遥测</td><td colspan="2">支持</td></tr><tr><td>输入信号</td><td colspan="2">DSHOT 150/300/600/1200/2400, MultiShot, OneShot </td></tr><tr><td>输出电压</td><td colspan="2">VBAT（为飞控供电）</td></tr><tr><td>最大电流</td><td colspan="2">每通道超过 70A（连续）/ 100A（突发）</td></tr><tr><td>电流传感器</td><td colspan="2">比例 = 170，偏移 = 0</td></tr><tr><td>双面焊盘</td><td colspan="2">是</td></tr><tr><td>尺寸</td><td colspan="2">33x43mm</td></tr><tr><td>安装</td><td colspan="2">20x20mm, Φ4mm, M3 橡胶垫圈</td></tr><tr><td>重量</td><td colspan="2">13.4g</td></tr></table><div class="page"></div><h2 id="包含物" tabindex="-1">包含物 <a class="header-anchor" href="#包含物" aria-label="Permalink to “包含物”">​</a></h2><ul><li><p>1x HDZero 电调</p></li><li><p>5x 橡胶垫圈（4.5mm 高）</p></li><li><p>5x 橡胶垫圈（6.6mm 高）</p></li><li><p>5x 尼龙垫圈（M3 1.0mm 厚）</p></li><li><p>5x 304 不锈钢螺母（M3 2.3mm 厚）</p></li><li><p>1x 电调线缆（-pin SH1.0, 30mm）</p></li><li><p>1x 8-pin SH1.0 连接器</p></li><li><p>1x XT60 线缆（70mm,12AWG）</p></li><li><p>1x 35V/1000uf 电容</p></li></ul>',12)])])}const x=a(e,[["render",o]]);export{_ as __pageData,x as default};
